    <script>
         function togglePassword() {
                const passwordField = document.getElementById("password");
                const icon = document.querySelector(".toggle-password i");
                if (passwordField.type === "password") {
                    passwordField.type = "text";
                    icon.classList.replace("fa-eye", "fa-eye-slash");
                } else {
                    passwordField.type = "password";
                    icon.classList.replace("fa-eye-slash", "fa-eye");
                }
            }

    </script>


<script>
    document.getElementById('loginForm').addEventListener('submit', function(event) {
    event.preventDefault(); // Prevent default form submission

    // Get values from the form inputs
    const username = document.getElementById('username').value;
    const password = document.getElementById('password').value;

    // Send a POST request to login.php
    fetch('db_connection.php', {
        method: 'POST',
        body: new URLSearchParams({
            username: username,  // Dynamic username from form
            password: password   // Dynamic password from form
        })
    })
    .then(response => response.json())  // Parse JSON response
    .then(data => {
        if (data.status === 'success') {
            console.log('Login Successful:', data.message);
            window.location.href = './dashboard/dashboard.php';  // Redirect to dashboard on success
        } else {
            console.error('Login Error:', data.message);  // Log error to the console
        }
    })
    .catch(error => {
        console.error('Error during fetch request:', error);
    });

    // fetch('/api/user.php') 
